        };
      });
    },
    getFilteredProposalsByStatusAndkind: ({
      resPerPage,
      reverse,
      filterKindArray,
      filterStatusArray,
      offset,
    }) => {
      let newLastProposalId = offset ?? 0;
      let filteredProposals = [];
      const lastProposalId = DaoSDK.getLastProposalId();
      const limit = 30;
      if (reverse && !offset) {
        newLastProposalId = lastProposalId;
      }
      const promiseArray = [];
      while (
        (reverse && newLastProposalId > 0) ||
