      ];
      if (Array.isArray(additionalCalls)) {
        calls = calls.concat(additionalCalls);
      }
      return Near.call(calls);
    },
    // SPECIFIC PROPOSALS
    createAddMemberProposal: ({
      description,
      memberId,
      roleId,
      gas,
      deposit,
