    },
    createPollProposal: ({ description, gas, deposit, additionalCalls }) => {
      return DaoSDK.addProposal({
        proposal: {
          description: description,
          kind: "Vote",
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    createTransferProposal: ({
      description,
      tokenId,
      receiverId,
      amount,
      gas,
      deposit,
      additionalCalls,
    }) => {
      return DaoSDK.addProposal({
        proposal: {
          description: description,
          kind: {
            Transfer: {
              token_id: tokenId,
              receiver_id: receiverId,
              amount,
            },
          },
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    createBountyProposal: ({
      description,
      bounty,
      gas,
      deposit,
      additionalCalls,
    }) => {
      return DaoSDK.addProposal({
        proposal: {
          description: description,
          kind: {
            AddBounty: {
              bounty,
            },
          },
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    createSubmitBountyProposal: ({
      description,
      bounty,
      receiverId,
      gas,
      deposit,
      additionalCalls,
    }) => {
      return DaoSDK.addProposal({
        proposal: {
          description: description,
          kind: {
            BountyDone: {
              receiver_id: receiverId,
              bounty_id: JSON.parse(bounty.id),
            },
          },
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    createFunctionCallProposal: ({
      description,
      methodName,
      receiverId,
      args,
      proposalGas,
      proposalDeposit,
      gas,
      deposit,
      additionalCalls,
    }) => {
      const proposal_args = Buffer.from(JSON.stringify(args), "utf-8").toString(
        "base64"
      );
      return DaoSDK.addProposal({
        proposal: {
          description: description,
          kind: {
            FunctionCall: {
              receiver_id: receiverId,
              actions: [
                {
                  method_name: methodName,
                  args: proposal_args,
                  deposit: proposalDeposit,
                  gas: proposalGas,
                },
              ],
            },
          },
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    // VOTE
    actProposal: ({ proposalId, action, deposit, gas, additionalCalls }) => {
      return DaoSDK.call({
        methodName: "act_proposal",
        args: {
          id: proposalId,
          action,
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    // SPECIFIC VOTE TXN
    approveProposal: ({ proposalId, deposit, gas, additionalCalls }) => {
      return DaoSDK.actProposal({
        proposalId,
        action: DaoSDK.voteActions.VoteApprove,
        deposit,
        gas,
        additionalCalls,
      });
    },
    rejectProposal: ({ proposalId, deposit, gas, additionalCalls }) => {
      return DaoSDK.actProposal({
        proposalId,
        action: DaoSDK.voteActions.VoteReject,
        deposit,
        gas,
        additionalCalls,
      });
    },
    removeProposal: ({ proposalId, deposit, gas, additionalCalls }) => {
      return DaoSDK.actProposal({
        id: proposalId,
        action: DaoSDK.voteActions.VoteRemove,
        deposit,
        gas,
        additionalCalls,
      });
    },
    // BOUNTIES
    claimBounty: ({ bounty, gas, deposit, additionalCalls }) => {
      return DaoSDK.call({
        methodName: "bounty_claim",
        args: {
          id: JSON.parse(bounty.id),
          deadline: bounty.max_deadline,
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
    unclaimBounty: ({ bounty, gas, deposit, additionalCalls }) => {
      return DaoSDK.call({
        methodName: "bounty_giveup",
        args: {
          id: JSON.parse(bounty.id),
        },
        deposit,
        gas,
        additionalCalls,
      });
    },
  };
  return DaoSDK;
};

sdks.near/widget/EthereumSigner.code:
const EthereumSigner = {
  sign: (message) => Ethers.provider().getSigner().signMessage(message),
  verify: (originalMessage, signature, expectedSignerAddress) => {
    return new Promise((resolve, reject) => {
      try {
        const address = EthereumSigner.getSignerAddress(
          originalMessage,
          signature
        );
        return resolve(
          expectedSignerAddress.toLowerCase() == address.toLowerCase()
        );
      } catch {
        console.error(
          "The signature is invalid or the expectedSignerAddress is null"
        );
        return reject();
      }
    });
  },
  recoverPublicKey: (originalMessage, signature) => {
    return (
      ethers.utils.recoverPublicKey(
        ethers.utils.hashMessage(originalMessage),
        signature
      ) || ""
    ).substring(4);
  },
  getSignerAddress: (message, signature) => {
    return ethers.utils.verifyMessage(message, signature);
  },
};
return EthereumSigner;

sdks.near/widget/EthereumVerifier.code:
const $ = VM.require("sdks.near/widget/Loader");
const { EthereumSigner } = $("@sdks/eth-signer");
const EthereumVerifier = {
  createProof: (nearAccount, address) => {
    return EthereumVerifier.sign(nearAccount, address).then((signature) => {
      return {
        address,
        signature,
      };
    });
  },
  sign: (nearAccount, address) => {
    return EthereumSigner.sign(
      EthereumVerifier.getChallenge(nearAccount, address)
    );
  },
  verify: (nearAccount, address, signature) => {
    const message = EthereumVerifier.getChallenge(nearAccount, address);
    return EthereumSigner.verify(message, signature, address);
  },
  getChallenge: (nearAccount, address) => {
    return `${alias / nearAccount.toLowerCase()} owns the Ethereum address ${
      alias / address.toLowerCase()
    }`;
  },
};
return EthereumVerifier;

sdks.near/widget/ExternalDependency.code:
return (props) => {
  const code = `
        <script>
            const ping = () => {
                window.top.postMessage("ping", "*");
            }
        </script>
        <script type="text/javascript" src="https://unpkg.com/${
          alias / props.adapter.package
        }"></script>
        <script type="text/javascript">
            if (window["${alias / props.adapter.name}"]) {
                ping();
            }

            const getPackageMethod = (instruction, package) => {
                return typeof package != "function" ? instruction.split(".").reduce((path, nextPath) => (path || {})[nextPath], package) : package;
            };

            window.addEventListener("message", (e) => {
              if (e.data.instruction) {
                 let value = Array.isArray(e.data.value) ? e.data.value : [e.data.value];
                 let result = getPackageMethod(e.data.instruction, window["${
                   alias / props.adapter.name
                 }"])(...value);
                 let parseResult = (result) => {
                    if (e.data.returnType == "object" || e.data.returnType == "array") {
                        return JSON.stringify(result);
                    }

                    if (e.data.returnType == "string") {
                        return result.toString();
                    }
                    if (e.data.returnType == "number") {
                        return result;
                    }
                    if (e.data.returnType == "canvas") {
                        return result.toDataURL();
                    }
                 }

                 window.top.postMessage({
                    index: e.data.index,
                    instruction: e.data.instruction,
                    returnType: e.data.returnType,
                    result: parseResult(result)
                 }, "*");
              }
            })
        </script>
   `;
  return (
    <iframe
      srcDoc={code}
      style={{ display: "none" }}
      message={props.adapter.getRequest()}
      onMessage={(data) =>
        data == "ping"
          ? props.adapter.setAsReady()
          : props.adapter.setResponse(data)
      }
    />
  );
};

