      txIdToTxHash: (txIdToTxHashRequest) =>
        LensSDK._call(
          TransactionAPI.txIdToTxHash,
          TransactionRequests.TXID_TO_TXHASH_REQUEST,
          txIdToTxHashRequest
        ),
    },
    customRequest: (graphql, request) => {
      LensSDK.set("requestInProgress", true);
      return LightClient.graphql(graphql, request)
        .then((data) => data.body)
        .finally(() => {
          LensSDK.set("requestInProgress", false);
        });
    },
    _call: (apiMethod, requestObject, dataObject) => {
      LensSDK.set("requestInProgress", true);
      return apiMethod(
        LightClient,
        dataObject ? ApiHelper.intersect(requestObject, dataObject) : null
      ).finally(() => {
        LensSDK.set("requestInProgress", false);
      });
    },
    updateAuth: (auth) => {
      LensSDK.set("auth", auth);
      LensSDK.persist("auth", auth);
    },
    clearAuth: () => {
      LensSDK.set("auth", Interfaces.AUTH_INTERFACE);
      LensSDK.persist("auth", Interfaces.AUTH_INTERFACE);
    },
    tryGetAuth: () => {
      if (LensSDK.get("tryGetAuth")) {
        LensSDK.getPersisted("auth", Interfaces.AUTH_INTERFACE);
        LensSDK.getPersisted("profileId", "");
        setTimeout(() => {
          let auth = LensSDK.getPersisted("auth", Interfaces.AUTH_INTERFACE);
          let profileId = LensSDK.getPersisted("profileId", "");
          if (auth) {
            LensSDK.set("auth", auth);
          } else {
            setTimeout(() => {
              let auth = LensSDK.getPersisted(
                "auth",
                Interfaces.AUTH_INTERFACE
              );
              if (auth) {
                LensSDK.set("auth", auth);
              }
            }, 800);
          }
          if (profileId) {
            LensSDK.profile
              .fetch({
                forProfileId: profileId,
              })
              .then((profile) => {
                LensSDK.set("profile", profile);
              });
          } else {
            setTimeout(() => {
              let profileId = LensSDK.getPersisted("profileId", "");
              if (profileId) {
                LensSDK.profile
                  .fetch({
                    forProfileId: profileId,
                  })
                  .then((profile) => {
                    LensSDK.set("profile", profile);
                  });
              }
            }, 800);
          }
        }, 800);
        LensSDK.set("tryGetAuth", false);
      }
    },
    persist: (key, value) => {
      Storage.privateSet(LensSDK.getPersistKey(key), JSON.stringify(value));
    },
    getPersisted: (key, defaultValue) => {
      let persistedKey = LensSDK.getPersistKey(key);
      return JSON.parse(Storage.privateGet(persistedKey)) || defaultValue;
    },
    getPersistKey: (key) => `LensSDK.${alias / key}`,
    getVersion: () => LensSDK.version,
  };
  return LensSDK.init();
};

sdks.near/widget/LensVerifier.code:
const $ = VM.require("sdks.near/widget/Loader");
const { EthereumSigner } = $("@sdks/eth-signer");
const LensSDK = VM.require(`mattb.near/widget/LensSDKRequire`);
const LensVerifier = {
  authenticate: (address) => {
    return LensSDK.authenticateLens(address, () =>
      Ethers.provider().getSigner()
    );
  },
  createProof: (address, nearAccount) => {
    return LensVerifier.authenticate(address).then((success) => {
      if (success) {
        return LensSDK.getProfileByEthereumAddress(address).then((payload) => {
          let [profile] = payload.body.data.profiles.items;
          let handle = profile.handle;
          if (!handle) {
            return new Promise((_, reject) =>
              reject("This address doesn't own a handle or the request failed")
            );
          }
          return LensVerifier.sign(handle, nearAccount, address).then(
            (signature) => {
              return {
                handle,
                signature,
              };
            }
          );
        });
      }
      return new Promise((_, reject) => reject("Lens authentication failed"));
    });
  },
  sign: (handle, nearAccount, address) => {
    return EthereumSigner.sign(
      LensVerifier.getChallenge(handle, nearAccount, address)
    );
  },
  verify: (handle, nearAccount, signature) => {
    return LensSDK.getProfileByHandle(handle).then((payload) => {
      let expectedAddress = payload.body.data.profile.ownedBy;
      const message = LensVerifier.getChallenge(
        handle,
        nearAccount,
        expectedAddress
      );
      return EthereumSigner.verify(message, signature, expectedAddress);
    });
  },
  getChallenge: (handle, nearAccount, address) => {
    return `${alias / nearAccount.toLowerCase()} with address ${
      alias / address.toLowerCase()
    } owns the ${alias / handle.toLowerCase()} handle`;
  },
};
return LensVerifier;

