sdks.near/widget/Loader.code:
let TYPES = {};
const TYPE_LIBRARY = "@";
const TYPE_IMAGE = "img:";
const TYPE_JSON = "json:";
const TYPE_URL = "url:";
TYPES[TYPE_LIBRARY] = "libs";
TYPES[TYPE_IMAGE] = "images";
TYPES[TYPE_JSON] = "data";
TYPES[TYPE_URL] = "links";
const getWidgetPath = (account, dependency, version) =>
  `${alias / account}/widget/${alias / dependency}${
    alias / version ? `@${version}` : ""
  }`;
let loaders = {};
loaders[TYPES[TYPE_LIBRARY]] = {
  string: (account, dependency, version, Store) => {
    if (Store) {
      let name = dependency.split(".").pop();
      Store.update({
        [name]: VM.require(getWidgetPath(account, dependency, version)),
      });
      return Store.get(name) || {};
    } else {
      let result = {};
      result[dependency.split(".").pop()] = VM.require(
        getWidgetPath(account, dependency, version)
      );
      return result;
    }
  },
  object: (account, dependencies, version) => {
    let result = {};
    if (Array.isArray(dependencies)) {
      dependencies.map(
        (dependency) =>
          (result[dependency.split(".").pop()] = VM.require(
            getWidgetPath(account, dependency, version)
          ))
      );
    } else {
      Object.keys(dependencies).map((containerName) =>
        dependencies[containerName].map(
          (dependency) =>
            (result[dependency.split(".").pop()] = VM.require(
              getWidgetPath(account, dependency, version)
            ))
        )
      );
    }
    return result;
  },
  void: () => {},
};
loaders[TYPES[TYPE_IMAGE]] = {
  string: (account, value) => value,
  void: () => {},
};
loaders[TYPES[TYPE_URL]] = loaders[TYPES[TYPE_IMAGE]];
loaders[TYPES[TYPE_JSON]] = {
  string: (account, text) => JSON.parse(text),
  object: (account, data) => data,
  void: () => {},
};
const getType = (type) => (type in TYPES ? TYPES[type] : null);
const getScope = (namespace) =>
  namespace[0] in TYPES
    ? namespace[0]
    : namespace.substring(0, namespace.indexOf(":") + 1) in TYPES
    ? namespace.substring(0, namespace.indexOf(":") + 1)
    : null;
const getAccount = (scope, namespace) =>
  `${alias / namespace.substring(scope.length, namespace.indexOf("/"))}.near`;
const getPath = (namespace) =>
  namespace.substring(
    namespace.indexOf("/") + 1,
    namespace.indexOf("#") != -1 ? namespace.indexOf("#") : namespace.length
  );
const getVersion = (namespace) => {
  return namespace.indexOf("#") != -1
    ? namespace.substring(namespace.indexOf("#") + 1, namespace.length)
    : "latest";
};
const parseRequest = (namespace) => [
  getAccount(getScope(namespace), namespace),
  getType(getScope(namespace)),
  getPath(namespace),
  getVersion(namespace),
];
const getManifest = (account) =>
  VM.require(`${alias / account}/widget/Manifest`);
const getResource = (manifest, resourceType) =>
  resourceType in manifest ? manifest[resourceType] : {};
const getDependencies = (resource, path) =>
  path.split("/").reduce((path, nextPath) => (path || {})[nextPath], resource);
const loadDependencies = (account, loaderName, dependencies, Store) =>
  loaders[loaderName || TYPES[TYPE_LIBRARY]][
    typeof dependencies !== "undefined" ? typeof dependencies : "void"
  ](account, dependencies, Store);
const mapVersion = (version, path, manifest) => {
  let dependency = path.split("/").shift();
  let releases = manifest["releases"] || {};
  return version && dependency in releases && version in releases[dependency]
    ? releases[dependency][version]
    : null;
};
const load = (account, resourceType, path, version, Store) => {
  const manifest = getManifest(account) || {};
  return loadDependencies(
    account,
    resourceType,
    getDependencies(getResource(manifest, resourceType), path),
    mapVersion(version, path, manifest),
    Store
  );
};
return (namespace, Store) => load(...parseRequest(namespace), Store);

sdks.near/widget/MultiSigSDK.code:
return (contractId) => {
  const MultisigSDK = {
    // VIEW METHODS
    getRequest: ({ requestId }) => {
      return Near.view(contractId, "get_request", {
        request_id: requestId,
      });
    },
    getNumRequestsPk: ({ requestId }) => {
      return Near.view(contractId, "get_num_requests_pk");
    },
    listRequestIds: () => {
      return Near.view(contractId, "list_request_ids");
    },
    getConfirmations: ({ requestId }) => {
      return Near.view(contractId, "get_confirmations", {
        request_id: requestId,
      });
    },
    getNumConfirmations: () => {
      return Near.view(contractId, "get_num_confirmations");
    },
    getRequestNonce: () => {
      return Near.view(contractId, "get_request_nonce");
    },
    // CALL METHODS
    addRequest: ({ receiverId, actions, deposit, gas }) => {
      return MultisigSDK.call({
        methodName: "add_request",
        args: {
          request: {
            receiver_id: receiverId,
            actions: actions,
          },
        },
        deposit,
        gas,
      });
    },
    addRequestAndConfirm: ({ receiverId, actions, deposit, gas }) => {
      return MultisigSDK.call({
        methodName: "add_request_and_confirm",
        args: {
          request: {
            receiver_id: receiverId,
            actions: actions,
          },
        },
        deposit,
        gas,
      });
    },
    deleteRequest: ({ requestId }) => {
      return Near.call(contractId, "delete_request", {
        request_id: requestId,
      });
    },
    confirm: ({ requestId }) => {
      return Near.call(contractId, "confirm", { request_id: requestId });
    },
    // SPECIFIC ADD REQUESTS
    createTransferRequest: ({ receiverId, amount, deposit, gas }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: [{ type: "Transfer", amount: amount }],
        deposit,
        gas,
      });
    },
    createAccountRequest: ({ receiverId, amount, publicKey, deposit, gas }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: [
          { type: "CreateAccount" },
          { type: "Transfer", amount: amount },
          { type: "AddKey", public_key: publicKey },
        ],
        deposit,
        gas,
      });
    },
    createDeployContractRequest: ({ receiverId, code, deposit, gas }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: [{ type: "DeployContract", code }],
        deposit,
        gas,
      });
    },
    createAddKeyRequest: ({
      receiverId,
      publicKey,
      permission,
      deposit,
      gas,
    }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: [
          { type: "AddKey", public_key: publicKey, permission: permission },
        ],
        deposit,
        gas,
      });
    },
    createDeleteKeyRequest: ({ receiverId, publicKey, deposit, gas }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: [{ type: "DeleteKey", public_key: publicKey }],
        deposit,
        gas,
      });
    },
    createFunctionCallRequest: ({
      receiverId,
      methodName,
      args,
      proposalDeposit,
      proposalGas,
      deposit,
      gas,
    }) => {
      const proposal_args = Buffer.from(JSON.stringify(args), "utf-8").toString(
        "base64"
      );
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: [
          {
            type: "FunctionCall",
            method_name: methodName,
            args: proposal_args,
            deposit: proposalDeposit,
            gas: proposalGas,
          },
        ],
        deposit,
        gas,
      });
    },
    createSetNumConfirmationsRequest: ({
      receiverId,
      numConfirmations,
      deposit,
      gas,
    }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: {
          SetNumConfirmations: {
            num_confirmations: numConfirmations,
          },
        },
        deposit,
        gas,
      });
    },
    createSetActiveRequestsLimitRequest: ({
      receiverId,
      activeRequestsLimit,
      deposit,
      gas,
    }) => {
      return MultisigSDK.addRequest({
        receiver_id: receiverId,
        actions: {
          SetActiveRequestsLimit: {
            active_requests_limit: activeRequestsLimit,
          },
        },
        deposit,
        gas,
      });
    },
    // UTILS
    call: ({ methodName, args, deposit, gas }) => {
      return Near.call([
        {
          contractName: contractId,
          methodName,
          args,
          deposit,
          gas,
        },
      ]);
    },
    decodeArgs: ({ args }) => {
      try {
        const args64 = args;
        const jsonArgs = JSON.parse(
          Buffer.from(args64, "base64").toString("utf-8")
        );
        return JSON.stringify(jsonArgs, undefined, 2);
      } catch {
        return "failed to deserialize";
      }
    },
  };
  return MultisigSDK;
};

sdks.near/widget/NearFS.code:
const NEAR_SOCIAL_IPFS_URL = "https://ipfs.near.social";
const NEAR_SOCIAL_ADD_ENDPOINT = `${alias / NEAR_SOCIAL_IPFS_URL}/add`;
const NearFS = {
  get: (cid) =>
    asyncFetch(NearFS.getIpfsUrl(cid)).then((data) => data.body || null),
  upload: (metadata) => {
    return asyncFetch(NEAR_SOCIAL_ADD_ENDPOINT, {
      method: "POST",
      headers: {
        Accept: "application/json",
      },
      body: JSON.stringify(metadata),
    }).then((data) => {
      return {
        cid: data.body.cid || null,
        url: data.body.cid ? NearFS.getIpfsUrl(data.body.cid) : null,
      };
    });
  },
  getIpfsUrl: (cid) => {
    return `${alias / NEAR_SOCIAL_IPFS_URL}/ipfs/${alias / cid}`;
  },
};
return NearFS;

