// Only works on Lens Testnet for now
const CREATE_PROFILE_MUTATION = `
    mutation CreateProfile($createProfileWithHandleRequest: CreateProfileWithHandleRequest!) {
      createProfileWithHandle(
        request: $createProfileWithHandleRequest
      ) {
        ... on RelaySuccess {
          txHash
        }
        ... on CreateProfileWithHandleErrorResult {
          reason
        }
      }
    }
`;
const PROFILE_REPORT_MUTATION = `mutation ReportProfile($reportProfileRequest: ReportProfileRequest!) {
  reportProfile(request: $reportProfileRequest)
}`;
const PROFILE_BLOCK_MUTATION = `mutation CreateBlockProfilesTypedData($options: TypedDataOptions, $blockProfileRequest: BlockRequest!) {
  createBlockProfilesTypedData(options: $options, request: $blockProfileRequest) {
    id
    expiresAt
    typedData {
      value {
        nonce
        deadline
        byProfileId
        idsOfProfilesToSetBlockStatus
        blockStatus
        __typename
      }
      domain {
        name
        chainId
        version
        verifyingContract
        __typename
      }
      types {
        SetBlockStatus {
          name
          type
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}`;
return {
  CREATE_PROFILE_MUTATION,
  PROFILE_REPORT_MUTATION,
  PROFILE_BLOCK_MUTATION,
};

