const Container = styled.div`
  margin: 0 auto;
  padding: 20px;
  width: 100%;
  max-width: 1200px;
  a {
    text-decoration: none;
    color: inherit;
  }
`;
const Grid = styled.div`
  display: grid;
  grid-gap: 10px;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  @media (min-width: 576px) {
    grid-gap: 15px;
  }
  @media (min-width: 992px) {
    grid-gap: 20px;
  }
  > * {
    transition: transform 0.3s ease; // Smooth transition for hover effect
    &:hover {
      transform: scale(1.03); // Subtle scale effect on hover
    }
  }
`;
const processData = useCallback(
  (data) => {
    const accounts = Object.entries(data);
    const allItems = accounts
      .map((account) => {
        const accountId = account[0];
        const plugins = Object.entries(account[1].plugin.embed);
        if (plugins.length > 0) {
          return plugins.map((kv) => {
            return {
              accountId,
              name: kv[0],
              metadata: Social.get(
                `${alias / accountId}/plugin/embed/${
                  alias / kv[0]
                }/metadata/**`,
                "final"
              ),
            };
          });
        } else {
          return {
            accountId,
            name: account[1].plugin.embed,
            metadata: Social.get(
              `${alias / accountId}/plugin/embed/${
                alias / account[1].plugin.embed
              }/metadata/**`,
              "final"
            ),
          };
        }
      })
      .flat();
    // sort by latest
    allItems.sort((a, b) => b.blockHeight - a.blockHeight);
    return allItems;
  },
  [type]
);
const items = processData(plugins);
if (!items) {
  return "Loading data...";
}
function Item({ accountId, name, metadata }) {
  return (
    <Widget
      src="embeds.near/widget/EmbedPlugin"
      props={{ accountId, name, type, metadata }}
