    <ContextMenu.Root>
      <ContextMenu.Trigger asChild>
        <div style={{ width: "100%" }}>
          <Item />
        </div>
      </ContextMenu.Trigger>
      <ContextMenu.Content sideOffset={5} align="end" asChild>
        <Wrapper>
          {handlers &&
            Object.keys(handlers).map((key) => {
              return (
                <ContextMenu.Item
                  className="menu__item"
                  onSelect={() => handlers[key](passProps[key])}
                >
                  {items[key]()}
                </ContextMenu.Item>
