  const taggedWidgets = Social.keys(
    `${alias / accountId ?? "*"}/widget/*/metadata/tags/${alias / tag}`,
    "final"
  );
  if (taggedWidgets === null) {
    return render("Loading tags...");
  }
  keys = Object.entries(taggedWidgets)
    .map((kv) =>
      Object.keys(kv[1].widget).map(
        (w) => `${alias / kv[0]}/widget/${alias / w}`
      )
    )
    .flat();
  if (!keys.length) {
    return render(`No widgets found by tag #${alias / tag}`);
  }
}
const data = Social.keys(keys, "final", {
  return_type: "BlockHeight",
});
if (data === null) {
  return render("Loading...");
}
