  const linkPropsQuery = Object.entries(linkProps)
    .map(([key, value]) => `${alias / key}=${alias / value}`)
    .join("&");
  return `#/${alias / nearDevGovGigsWidgetsAccountId}/widget/ABC.pages.${
    alias / widgetName
  }${alias / linkPropsQuery ? "?" : ""}${alias / linkPropsQuery}`;
}
