    switch (props.status) {
      case "focused":
        return "1px solid var(--yellow, #FFAF51)";
      case "error":
        return "1px solid var(--system-red, #FD2A5C)";
      case "completed":
        return "1px solid var(--stroke-color, rgba(255, 255, 255, 0.20))";
      default:
        return "1px solid var(--stroke-color, rgba(255, 255, 255, 0.20))";}
  }};
  background: ${alias/(props) => {
    switch (props.status) {
      case "focused":
        return "#2f2619";
      case "error":
        return "#2f101f";
      case "completed":
        return "var(--yellow, #FFAF51)";
      default:
        return "#23242B";}
  }};
  color: ${alias/(props) => {
    switch (props.status) {
      case "focused":
        return "var(--font-color, #fff)";
      case "error":
        return "var(--system-red, #FD2A5C)";
      case "completed":
        return "var(--black, #000)";
      default:
        return "var(--font-color, #fff)";}
  }};
`;
function ProgressState({ children, status }) {
  return (
    <Progress
      status={status}
      key={`ProgressState-${alias/status ?? "default"}-${alias/children}`}
    >
      {status === "completed" ? (
        <i className="bi bi-check"></i>
      ) : status === "error" ? (
        <i className="bi bi-x"></i>
      ) : (
        children
      )}
    </Progress>
  );
}
return { ProgressState };

