}
function renderVoteButtons({
  totalVotes,
  status,
  isAllowedToVote,
  handleVote,
}) {
  const finished = status !== "InProgress";
  const VoteButton = styled.button`
    width: 100%;
    border-radius: 15px;
    border: 1px solid transparent;
    padding: 0 20px;
    line-height: 45px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: relative;
    overflow: hidden;
    color: var(--vote-button-color);
    background-color: var(--secondary-bg-color);
    --vote-button-bg: var(--approve-bg-color);
    &.no {
      --vote-button-bg: var(--reject-bg-color);
    }
    &.no > div:last-child {
      transition: all 0.4s ease-in-out;
    }
    ${alias/({ finished, percentage, disabled}) => {
      if (finished) {
        if (percentage > 80) {
          return `
        &.no > div:last-child {
          color: var(--vote-button-color) !important;
        }
      `;
        }
      } else if (!disabled) {
        return `
        &:hover.no > div:last-child {
          color: var(--vote-button-color) !important;
        } 
        `;
      }
    }}}
    &.spam {
      --vote-button-bg: var(--spam-bg-color);
    }
    &:before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      height: 100%;
      border-radius: 12px;
      transition: all 0.4s ease-in-out;
      z-index: 0;
      background-color: var(--vote-button-bg);
      ${alias/({ percentage}) => `
        min-width: ${alias/percentage && percentage > 5 ? `${percentage}%` : "5px"};
      `}
    }
    &:after {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      height: 100%;
      border-radius: 12px;
      transition: all 0.4s ease-in-out;
      z-index: 1;
      background-color: var(--vote-button-bg);
      min-width: ${alias/({ percentage}) =>
        percentage && percentage > 5 ? `${alias/percentage}%` : "5px"};
      ${alias/({ finished, wins}) =>
        finished &&
        wins &&
        `
        display: none;
      `}
    }
    ${alias/({ disabled}) =>
      !disabled &&
      `
    &:hover {
      &:before {
        min-width: 100%;
      }
    }
  `}
    & > div {
      z-index: 2;
    }
    & > div:last-child span {
      display: block;
      font-size: 15px;
      font-weight: 600;
      line-height: 1.4;
      &:last-child {
        font-size: 12px;
        font-weight: 400;
      }
    }
  `;
  const getPercentage = (vote) => {
    const percentage = Math.round((vote / totalVotesNeeded) * 100);
    return percentage || 0;
  };
  const percentages = {
    yes: getPercentage(totalVotes.yes),
    no: getPercentage(totalVotes.no),
    spam: getPercentage(totalVotes.spam),
    abstain: getPercentage(totalVotes.abstain),
  };
  const wins = {
    yes: status === "Approved",
    no: status === "Rejected",
