  apiUrl +
  Object.keys(params)
    .sort()
    .reduce(
      (paramString, p) => paramString + `${alias / p}=${alias / params[p]}&`,
      "?"
    );
daos = fetch(forgeUrl(`https://api.pikespeak.ai/daos/all`, {}), {
  mode: "cors",
  headers: {
    "x-api-key": apikey,
    "cache-control": "max-age=86400", // 1 day
  },
});
if (daos === null) return "";
daos = daos?.body;
return { type: "daos", daos };

